import React,{Component} from 'react';
import Properties from './components/properties';
import './App.css';

class App extends Component {
   constructor(props){
    super(props);
    this.state = {
      title: 'Phonebook',
      act: 0,
      index: '',
      id: '',
      datas: []
    }
  }

  componentDidMount(){
    this.fetchContactDetails();
    this.refs.name.focus();
  }

  fSubmit = async (e) => {
      e.preventDefault();
      console.log('try');

      let datas = this.state.datas;
      let name = this.refs.name.value;
      let mobile_number = this.refs.mobile_number.value;
      let serverSendData = {
          name, mobile_number
      }

      if (this.state.act === 0) { //new
          let data = {
              name, mobile_number
          }

          datas.push(data);
      } else {                    //update
          let index = this.state.index;
          datas[index].name = name;
          datas[index].mobile_number = mobile_number;
      }

      this.setState({
          datas: datas
      });
      if(this.state.id){
          this.editContactDetail(this.serverSendData)
      }
      const response = await fetch(`http://localhost:4000/create`, {
          method: "POST",
          mode: "cors",
          cache: "no-cache",
          headers: {
              "Content-Type": "application/json"
          },
          body: JSON.stringify(serverSendData)
      });

      this.refs.myForm.reset();
      this.refs.name.focus();
  }

  fetchContactDetails = () => {
    fetch(`http://localhost:4000/allcontacts`)
    .then(res => res.json())
    .then((data) => {
      this.setState({
          datas: data
      });
    })
    .catch(console.log)
  }

  editContactDetail = (serverSendData) => {
    fetch(`http://localhost:4000/images/?contactId=${this.state.id}`, {
          method: "PUT",
          mode: "cors",
          cache: "no-cache",
          headers: {
              "Content-Type": "application/json"
          },
          body: JSON.stringify(serverSendData)
      });
  }

  fRemove = (i) => {
    let datas  = this.state.datas;
    datas.splice(i,1);
    this.setState({
      datas:datas
    });

    this.refs.myForm.reset();
    this.refs.name.focus();
  }

  fEdit = (i, id) => {
    let data = this.state.datas[i];
    this.refs.name.value = data.name;
    this.refs.mobile_number.value = data.mobile_number;

    this.setState({
      act: 1,
      index: i,
      id: id
    })

    this.refs.name.focus();
  }

  render() {
    let datas = this.state.datas;
    return (
      <div className="App">
        <h2>{this.state.title}</h2>
        <form ref="myForm" className="myForm">
          <input type="text" ref="name" placeholder="name" className="formField" />
          <input type="text" ref="mobile_number" placeholder="mobile number" className="formField" />
          <button onClick={(e)=>this.fSubmit(e)} className="myButton">Submit</button>
        </form>
        <pre>
          {datas.map((data, i) =>
            <li key={i} className="myList">
              {data.id}.{data.name}, {data.mobile_number}
              <button onClick={()=>this.fRemove(i)} className="myListButton">Remove</button>
              <button onClick={()=>this.fEdit(i, data.id)} className="myListButton">Edit</button>
            </li>
          )}
        </pre>
      </div>
    );
  }
}

export default App;
