import React,{Component} from 'react';
import './App.css';

class App extends Component {
   constructor(props){
    super(props);
    this.state = {
      title: 'Add Contact',
      act: 0,
      index: '',
      id: '',
      datas: []
    }
  }

  componentDidMount(){
    this.fetchContactDetails();
    this.refs.name.focus();
  }

  fSubmit = async (e) => {
      e.preventDefault();
      let datas = this.state.datas;
      console.log(this.state.refs);
      let name = this.refs.name.value;
      let mobile_number = this.refs.mobile_number.value;
      let serverSendData = {
          name, mobile_number
      }

      if (this.state.id){
          this.editContactDetail(serverSendData);
      }
      else {
          const response = await fetch(`http://localhost:4000/create`, {
              method: "POST",
              mode: "cors",
              cache: "no-cache",
              headers: {
                  "Content-Type": "application/json"
              },
              body: JSON.stringify(serverSendData)
          });
      }
      this.fetchContactDetails();
      this.refs.myForm.reset();
      this.refs.name.focus();
  }

  fetchContactDetails = () => {
    fetch(`http://localhost:4000/allcontacts`)
    .then(res => res.json())
    .then((data) => {
      this.setState({
          datas: data
      });
    })
    .catch(console.log)
  }

  editContactDetail = async (serverSendData) => {
      const response = await fetch(`http://localhost:4000/editcontact/?contactId=${this.state.id}`, {
          method: "PUT",
          mode: "cors",
          cache: "no-cache",
          headers: {
              "Content-Type": "application/json"
          },
          body: JSON.stringify(serverSendData)
      });
  }

  deleteContactDetail = async (id) => {
      const response = await fetch(`http://localhost:4000/deletecontact/?contactId=${id}`, {
          method: "DELETE",
          mode: "cors",
          cache: "no-cache",
          headers: {
              "Content-Type": "application/json"
          },
          body: JSON.stringify()
      });
  }

  fRemove = (i, id) => {
    let datas  = this.state.datas;
    datas.splice(i,1);
    this.setState({
      datas:datas
    });
    this.deleteContactDetail(id);
    this.refs.myForm.reset();
    this.refs.name.focus();
  }

  fEdit = (i, id) => {
    let data = this.state.datas[i];
    this.refs.name.value = data.name;
    this.refs.mobile_number.value = data.mobile_number;

    this.setState({
      act: 1,
      index: i,
      id: id
    })

    this.refs.name.focus();
  }

  searchQuery = (e) => {
    let item = this.refs.query.value;
      console.log(item);
    fetch(`http://localhost:4000/searchcontact/?filterItem=${item}`)
    .then(res => res.json())
    .then((data) => {
      this.setState({
          datas: data
      });
    })
    .catch(console.log)
  }

  render() {
    let datas = this.state.datas;
    return (
      <div className="App">
        <h2>{this.state.title}</h2>
        <form ref="myForm" className="myForm" >
          <input type="text" ref="name" placeholder="name" className="formField" required/>
          <input type="number"
                 ref="mobile_number"
                 placeholder="mobile number"
                 className="formField"
                 pattern="/^(?:\+88|01)?(?:\d{11}|\d{13})$/"
                 required
          />
          <button onClick={(e)=>this.fSubmit(e)} className="myButton">Submit</button>
        </form>

        <h2>Contact List</h2>
        <div>
            <input type="text" ref="query" placeholder="search contact" className="searchbox" />
            <button onClick={(e)=>this.searchQuery(e)} className="enterbutton">Enter</button>
        </div>
        <pre className="contactList">
          {datas.map((data, i) =>
            <li key={i} className="myList">
              {data.id}.{data.name}, {data.mobile_number}
              <button onClick={()=>this.fRemove(i, data.id)} className="myListButton">Remove</button>
              <button onClick={()=>this.fEdit(i, data.id)} className="myListButton">Edit</button>
            </li>
          )}
        </pre>
      </div>
    );
  }
}

export default App;
